const create_svg = (contents, color) => {
	var svg_path = "";
	contents.forEach((p_dict) => {
		svg_path += `<`;
		svg_path += p_dict["content"];
		if (p_dict["fill"] != "") {
			svg_path += ` fill="${p_dict["color"]}"`;
		} else {
			svg_path += ` fill="${color}"`;
		}
		svg_path += `/>`;
	});
	return svg_path;
};

// 12.5pxから12.5とpxを分離
function pickUpIntUnitFromCss(cssStr) {
	const match = cssStr.match(/(\d*\.?\d+)([a-zA-Z%]+)/);
	return match ? { int: parseFloat(match[1]), unit: String(match[2]) } : { int: 0, unit: "px" };
}

// viewBoxからcss計算
function calcSvgRelativeHight(viewBoxStr, sizeCssStr) {
	const iconVBwidth = viewBoxStr.split(" ")[-2];
	const iconVBhight = viewBoxStr.split(" ")[-1];
	const sizedataDict = pickUpIntUnitFromCss(sizeCssStr);
	const svgRelativeHeight = (sizedataDict.int * iconVBhight) / iconVBwidth;
	const svgRelativeHeightRounded = svgRelativeHeight.toFixed(3); // 小数点以下3桁に丸める
	return svgRelativeHeightRounded + sizedataDict.unit;
}

const svgToBase64DataURL = (size, color, iconName) => {
	if (!(iconName in svg_dict)) {
		return "";
	}
	const iconDict = svg_dict[iconName];

	const svgRelativeHeightCssStr = calcSvgRelativeHight(iconDict["viewBox"], size);
	const svg_start = `<svg width="${size}" height="${svgRelativeHeightCssStr}" viewBox="${iconDict["viewBox"]}" xmlns="http://www.w3.org/2000/svg" version="1.1">`;
	const svg_path = create_svg(iconDict["contents"], color);
	const svg_end = `</svg>`;
	return `data:image/svg+xml,${encodeURIComponent(svg_start + svg_path + svg_end)}`;
};

const innerSvgHtml = (color, iconName) => {
	if (!(iconName in svg_dict)) {
		return {
			isError: true,
			innerHtml: "",
			viewBox: "0 0 0 0",
		};
	}
	const iconDict = svg_dict[iconName];
	const svg_path = create_svg(iconDict["contents"], color);
	return {
		isError: false,
		innerHtml: svg_path,
		viewBox: iconDict["viewBox"],
	};
};

module.exports = {
	svgToBase64DataURL,
	innerSvgHtml,
	calcSvgRelativeHight,
};
